function santa_hunt_init_scripts(){
    wp_enqueue_script( 'santa-hunt', '/wp-content/plugins/santa-hunt/santa-hunt.js', array(), 1.208, true );
}

add_action( 'wp_enqueue_scripts', 'santa_hunt_init_scripts' );


add_action( 'wp_ajax_nopriv_santa_hunt_collect', 'santa_hunt_collect' );
add_action( 'wp_ajax_santa_hunt_collect', 'santa_hunt_collect' );
function santa_hunt_collect() {
 
    if ( ! wp_verify_nonce( $_POST['nonce'], 'santa_hunt_collect_nonce' ) || ! isset( $_REQUEST['nonce'] ) ) {
        // if not, send error
        wp_send_json_error([
            'message' => 'No naughty business please'
            ]);
        wp_die();
    }
    $user_id = get_current_user_id();
    $item = sanitize_text_field($_POST['name']);
    $collection = get_user_meta( $user_id, '_santa_hunt', true );
    $allowed = ['hat', 'boots', 'tree', 'gift', 'list'];
    if(in_array($item, $allowed)){
        if(!is_array($collection)){
        $collection = [$item];
        }
        else if(!in_array($item, $collection)){
        $collection[]= $item;
        }
        update_user_meta($user_id, '_santa_hunt', $collection);
    }
 
    //send success response
    wp_send_json_success([
        'message' => $item . ' collected!',
        'collection' => $collection
        ]);
    
    wp_die();
}
function santa_hunt_does_user_have_item($item){
    $collection = get_user_meta(get_current_user_id(), '_santa_hunt', true);
    if(is_array($collection) && in_array($item, $collection)){
        return true;
    }
    else {
        return false;
    }
}

